import { Outlet, Link } from "react-router-dom";
import React,{useRef,useState,useEffect} from 'react';
import axios from "axios";

export default class CategoryNews extends React.Component {
    constructor(props) {
    super(props);
    // Don't call this.setState() here!
        this.state = { 
            cat_articles:[]
        };
    //this.handleClick = this.handleClick.bind(this);
    }

    componentDidMount(){
        console.log("Mount !!!");
        let tags 
        this.loadData();
    }

    loadData(){
        let self = this;
        let headers;
        headers = {

        'Content-Type': 'application/json',
        'Authorization': process.env.REACT_APP_API_TOKEN

        }

        let param = {}
        axios.post(process.env.REACT_APP_API_URL + "/api/cat-articles",param,{headers:headers})
        .then((res) => {
            // Set state with result
            this.setState(
            {
                cat_articles:res.data.data
            });
            console.log("Cat articles",this.state.cat_articles);

        })
        .catch(function (error) {
          if (error.response) {
            console.error(error);
          }
        });

    }
    render(){
        return(
            <div class="cat-news">
            <div class="container-fluid">
                <div class="row">
                { this.state.cat_articles &&  this.state.cat_articles.map((article, index) => (
                    <div class="col-md-6">
                        <h2><i class="fas fa-align-justify"></i>Sports</h2>
                        <div class="row cn-slider">
                            <div class="col-md-6">
                               <Article/>
                            </div>
                            <div class="col-md-6">
                                <div class="cn-img">
                                    <img src="img/cat-news-2.jpg" />
                                    <div class="cn-content">
                                        <div class="cn-content-inner">
                                            <a class="cn-date" href=""><i class="far fa-clock"></i>05-Feb-2020</a>
                                            <a class="cn-title" href="">Vestibulum ante ipsum primis</a>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div class="col-md-6">
                                <div class="cn-img">
                                    <img src="img/cat-news-3.jpg" />
                                    <div class="cn-content">
                                        <div class="cn-content-inner">
                                            <a class="cn-date" href=""><i class="far fa-clock"></i>05-Feb-2020</a>
                                            <a class="cn-title" href="">Sed quis convallis lacus</a>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                ))}
                </div>
            </div>
        </div>
        )
    }
}
