import axios from "axios";

export default class TopNews extends React.Component {
    constructor(props) {
    super(props);
    // Don't call this.setState() here!
     this.state = { 
         title:"Test",
         articles: [], 
         headlines:[]

    };
    //this.handleClick = this.handleClick.bind(this);
