import { Outlet, Link } from "react-router-dom";
import React,{useRef,useState,useEffect} from 'react';
import axios from "axios";
import Article from "./Article";
import { useSelector, useDispatch } from 'react-redux'
import {
  fetch,
  add,
  update
} from '../reducer/invoices/invoicesSlice'

export function Counter() {

    const count = useSelector(selectCount)
    const dispatch = useDispatch()
    const [incrementAmount, setIncrementAmount] = useState('2')

    return (
        <div class="invoices mt-3">
        <div class="container">
            <div class="row">
                <div class="col-md-12">
                    <div className="content-head mb-3">
                        <h2>Invoices </h2>
                        <a className="btn btn-success btn-lg" onClick={() => dispatch(add())}> + Add New </a>
                    </div>
                    <div className="content-body">
                        <table class="table">
                            <thead>
                                <tr>
                                    <th scope="col">ID</th>
                                    <th scope="col">Customer name</th>
                                    <th scope="col">Sale person name</th>
                                    <th scope="col">Sale date</th>
                                    <th scope="col">Products</th>
                                </tr>
                            </thead>
                            <tbody>
                                <tr>
                                    <th scope="row">1</th>
                                    <td>Mark</td>
                                    <td>Otto</td>
                                    <td>@mdo</td>
                                    <td></td>
                                </tr>

                            </tbody>
                        </table>
                    </div>

                </div>

            </div>
        </div>
    </div>
    )
  }

export default class ArticleList extends React.Component {
    constructor(props) {
    super(props);
    // Don't call this.setState() here!
        this.state = { 
            articles:[],
            name:"",
        };
    //this.handleClick = this.handleClick.bind(this);
    }

    componentDidMount(){

        let path = window.location.pathname;
        console.log(window.location);

        let pathval = path.split("/");
        console.log("pathVal",pathval);
        let type = pathval[1];
        let param = {};
        if(type == "tags")

            param.id = parseInt(pathval[2]);

        else if(type == "search"){

            const queryString = window. location. search;
            console.log(queryString);

            const parameters = new URLSearchParams(queryString);
            const value = parameters. get('keyword');
            param.keyword = value;

        }else if(type == "latest"){
            this.name = "Latest Articles";

        }else if(type == "popular"){
            this.name = "Popular Articles";
        }

        this.loadData(param);
    }

    loadData(param){

        let self = this;
        let headers;
        headers = {

        'Content-Type': 'application/json',
        'Authorization': process.env.REACT_APP_API_TOKEN

        }

        console.log("param",param);

        axios.post(process.env.REACT_APP_API_URL + "/api/articles",param,{headers:headers})
        .then((res) => {
            // Set state with result
            let name = this.name;
            if(res.data.data.name){
                name = res.data.data.name;
            }

            self.setState(
            {
                "articles":res.data.articles,
                "name":name
            });
        })
        .catch(function (error) {
          if (error.response) {
            console.error(error);
          }
        });

    }
    render(){
        return(
            <div class="invoices mt-3">
            <div class="container">
                <div class="row">
                    <div class="col-md-12">
                        <div className="content-head mb-3">
                            <h2>Invoices </h2>
                            <a className="btn btn-success btn-lg" onClick={() => dispatch(add())}> + Add New </a>
                        </div>
                        <div className="content-body">
                            <table class="table">
                                <thead>
                                    <tr>
                                        <th scope="col">ID</th>
                                        <th scope="col">Customer name</th>
                                        <th scope="col">Sale person name</th>
                                        <th scope="col">Sale date</th>
                                        <th scope="col">Products</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    <tr>
                                        <th scope="row">1</th>
                                        <td>Mark</td>
                                        <td>Otto</td>
                                        <td>@mdo</td>
                                        <td></td>
                                    </tr>

                                </tbody>
                            </table>
                        </div>

                    </div>

                </div>
            </div>
        </div>
        )
    }
}
