import { Outlet, Link } from "react-router-dom";
import React,{useRef,useState,useEffect} from 'react';
import axios from "axios";
import Article from "./Article";

export default class ArticleList extends React.Component {
    constructor(props) {
    super(props);
    // Don't call this.setState() here!
        this.state = { 
            articles:[],
            name:"",
        };
    //this.handleClick = this.handleClick.bind(this);
    }

    componentDidMount(){
        let path = window.location.pathname;
        let pathval = path.split("/");
        let id = parseInt(pathval[2]);
        let type = parseInt(pathval[1]);

        this.loadData(type,id);
    }

    loadData(type,id){

        console.log("type",type);
        console.log("id",id);

        let self = this;
        let headers;
        headers = {

        'Content-Type': 'application/json',
        'Authorization': process.env.REACT_APP_API_TOKEN

        }

        let param = {tag_id:id};
        console.log("param",param);

        axios.post(process.env.REACT_APP_API_URL + "/api/artticles",param,{headers:headers})
        .then((res) => {
            console.log("res",res);
            // Set state with result
            this.setState(
            {
                articles:res.data.articles,
                name:res.data.tag.name
            });


        })
        .catch(function (error) {
          if (error.response) {
            console.error(error);
          }
        });

    }
    render(){
        return(
            <div class="cat-news mt-3">
            <div class="container-fluid">
                <div class="row">
                    <div class="col-md-12">
                        <h2><i class="fas fa-align-justify"></i>{this.state.name}</h2>
                        <div class="row cn-slider">

                            { this.state.articles.map((article, index) => (
                                <div class="col-md-6">
                                    <Article article={article}/>
                                </div>
                            ))}

                        </div>
                    </div>

                </div>
            </div>
        </div>
        )
    }
}
